	def getMyProperties = {propertyLocation, mapToCreate
		->
		for (prop in propertyLocation.getPropertyList()) {
			propertyMapValue = propertyLocation.getPropertyValue(prop.getName())
			mapToCreate[prop.getName()] = propertyMapValue}
	}

